import React, { useState, useEffect } from "react";
import axios from "axios";
import{Link, useParams} from "react-router-dom";

export default function ProjectRequestModerator() {

    const[projects, setProjects] = useState([])

    useEffect(()=>{
        loadProject();
    }, [])

    const {id}=useParams()

    const loadProject=async ()=>{
        const result=await axios.get("http://localhost:8080/api/test/project/")
        setProjects(result.data);
    }

    const deleteProject=async (id)=>{
        await axios.delete(`http://localhost:8080/api/test/project/delete/${id}`)
        loadProject();
    }

    let c = projects.map(function (project){
        if(project.accepted !== null){
            return false;
        }
        return project;
    })

    const [project, setProject] = useState({
        accepted: 1
    })

    const onSubmit =async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/api/test/project/update/${id}`,project)
    };


    return(
        <div className='container'>
            <div className="py-4">
                <table className="table table-striped shadow">
                    <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Company</th>
                        <th scope="col">Contacts</th>
                        <th scope="col">Action</th>
                    </tr>
                    </thead>
                    <tbody>

                    {
                        c.map((project,index)=>(
                            <tr>
                                <th>{index+1}</th>
                                <td>{project.title}</td>
                                <td>{project.description}</td>
                                <td>{project.company}</td>
                                <td>{project.contact}</td>
                                <td>
                                    <form onSubmit = {(e)=>onSubmit(e)}>
                                        <button type="submit" className="btn btn-outline-success">Accept</button>
                                    </form>

                                </td>
                            </tr>
                        ))
                    }
                    </tbody>
                </table>
            </div>
        </div>
    )
}
